(84989, 3910, 'id', '26'),
(84990, 3910, 'latin_title', 'Trapelus sanguinolentus'),
(84991, 3910, 'trida', 'Plazi (Reptilia)'),
(84992, 3910, 'vyhledavani', 'Agama stepní'),
(84993, 3910, 'skupina', 'Šupinatí (Squamata)'),
(84994, 3910, 'popisek', 'Má poměrně velkou hlavu a mírně zploštělé tělo. Svrchu je hnědavá až šedohnědá, zespodu bělavá. Samci se při vzrušení barví spodina těla do sytě modré až šedočerné barvy. Obě pohlaví dokážou měnit zbarvení podle teploty okolí a podle fyziologického stavu.\r\n'),
(84995, 3910, 'obrazek', 'https://zoopraha.cz/images/lexikon/agama_stepni_foto_petr_velensky.jpg'),
(84996, 3910, 'obrazek_popis', 'Agama stepní, foto: Petr Velenský, Zoo Praha'),
(84997, 3910, 'kontinent', 'Asie'),
(84998, 3910, 'kontinent-detail', 'od Kaspického moře na východ po severozápadní Čínu a na jihu po Afganistán'),
