INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(85423, 3931, 'miry', 'délka těla 70–125 cm, z toho délka ocasu u samce až 75 cm, hmotnost 1,1–2 kg'),
(85424, 3931, 'rozmnozovani', 'Samice snáší 6–15 vajec a sedí na nich 26–27 dní.'),
(85425, 3931, 'zajimavosti', 'Je velmi otužilý, dobře se rozmnožuje a na péči o mláďata se často podílejí oba rodiče. V čínském umění se objevuje jako tzv. bílý Fénix. Kvůli jeho nenáročnosti a atraktivnímu vzhledu ho mají ve velké oblibě soukromí chovatelé.'),
(85426, 3931, 'chov', 'Bažant stříbrný patří v pražské zoo k poměrně novým druhům. Nominátní poddruh (L. n. nycthemera) chováme od roku 2006. Od r. 2012 přibyl pár vzácnějšího čínsko-thajského poddruhu bažanta stříbrného Jonesova (L. n. jonesi), který se vyskytuje od Myanmaru po jihozápadní Čínu a střední Thajsko. V Evropě ho chovají kromě Zoo Praha jen dvě další zoologické zahrady.\r\n '),
(85427, 3931, 'expozice', 'Dolní část zoo'),
(85428, 3931, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/areal/8660-dolni-cast-arealu'),
(85429, 3932, 'id', '49'),
(85430, 3932, 'latin_title', 'Crossoptilon crossoptilon'),
(85431, 3932, 'trida', 'Ptáci (Aves)'),
(85432, 3932, 'vyhledavani', 'Bažant tibetský'),
(85433, 3932, 'skupina', 'Hrabaví (Galliformes)'),
(85434, 3932, 'popisek', 'Patří do skupiny tzv. ušatých bažantů, jejichž typickým znakem jsou prodloužená pírka po stranách hlavy. Vytváří několik poddruhů, u všech se samec od samice téměř neliší.\r\n'),
(85435, 3932, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/BaIant_tibetsk_.jpg'),
(85436, 3932, 'obrazek_popis', 'Bažant tibetský, foto: Tereza Mrhálková, Zoo Praha'),
(85437, 3932, 'kontinent', 'Asie'),
(85438, 3932, 'kontinent-detail', 'jižní Asie – od Tibetu a severní Indie po jižní a střední Čínu'),
(85439, 3932, 'biotop', 'jehličnatý les'),
(85440, 3932, 'biotop-detail', 'jehličnaté i smíšené lesy a březové a pěnišníkové háje mezi 3 500 a 4 300 m n. m.'),
(85441, 3932, 'potrava', 'bezobratlí'),
(85442, 3932, 'potrava-detail', 'semena, výhonky a další části rostlin, bezobratlí'),
(85443, 3932, 'miry', 'délka těla 86–96 cm, délka ocasu 46–58 cm, hmotnost samců 2,3–2,75 kg, samic 1,4–2 kg'),
(85444, 3932, 'rozmnozovani', 'Samice snáší 4–9 vajec, na kterých sedí 24 dny – údaje z chovů.'),
(85445, 3932, 'zajimavosti', 'Silným zobákem doslova překopává půdu, aby získal potravu. Většinu roku tráví ve skupinkách až o třiceti ptácích, v době rozmnožování žije v párech. Je velmi otužilý a běžně se zdržuje i na zasněžených svazích. Dokáže úsporně přeletovat nízko nad sněhem, aby se dostal k dalšímu zdroji potravy. Při klouzavém letu využívá široký ocas.'),
(85446, 3932, 'chov', 'První bažant tibetský přišel do Prahy z „Durrellovy“ zoo na Jersey v r. 1977, žil v zoo do r. 1992 a pak odešel do Ostravy. Mezitím přicházeli do zoo další jedinci, mimo jiné v r. 1990 pár, který v r. 1992 odchoval historicky první mláďata.\r\nZimu tráví v zázemí zoo, mimo návštěvnické prostory.'),
(85447, 3932, 'expozice', 'Dolní část zoo'),
(85448, 3932, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/areal/8660-dolni-cast-arealu'),
(85449, 3933, 'id', '53'),
(85450, 3933, 'latin_title', 'Phasianus colchicus zarudnyi'),
(85451, 3933, 'trida', 'Ptáci (Aves)'),
(85452, 3933, 'vyhledavani', 'Bažant turkménský'),
(85453, 3933, 'skupina', 'Hrabaví (Galliformes)'),
(85454, 3933, 'popisek', 'Vzhledem se podobá nominátnímu poddruhu (bažantu kolchickému), ale nemá bílý obojek. Samec je pestrý, má hnědé tělo s tmavou kresbou, kovově lesklou zelenou hlavu a prodloužená ocasní pera. Samice je nevýrazně hnědá. Ona mají červenou lysinu na tvářích.\r\n'),
(85455, 3933, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/bazant_turkmensky_-_Tomas_Adamec.jpg'),
(85456, 3933, 'obrazek_popis', 'Bažant turkménský, foto: Tomáš Adamec, Zoo Praha'),
(85457, 3933, 'kontinent', 'Asie'),
(85458, 3933, 'kontinent-detail', 'Turkmenistán − malé území v údolích středního toku řeky Amudarji'),
(85459, 3933, 'biotop', 'travnaté území'),
(85460, 3933, 'biotop-detail', 'otevřená travnatá krajina s roztroušenými remízky a křovinami, kulturní krajina'),
(85461, 3933, 'potrava', 'rostliny i živočichové'),
(85462, 3933, 'potrava-detail', 'bezobratlí, semena, podzemní části rostlin, výhonky'),
(85463, 3933, 'miry', 'délka těla 42,5−53,5 cm, délka křídla 23,5−25,8 cm, hmotnost 900−1 200 g'),
(85464, 3933, 'rozmnozovani', 'Samice snáší do důlku na zemi 7−15 vajec, na kterých sedí sama 23−27 dní.'),
(85465, 3933, 'zajimavosti', 'Způsobem života se neliší od bažanta kolchického, který se vyskytuje jako nepůvodní druh i u nás. Samec je polygamní, v době toku láká samice hlasitým volání a zvláštním „tancem“ spojeným s třepetáním křídly a náznakovým zobáním.'),
(85466, 3933, 'chov', 'Poddruh chováme od r. 2011, v r. 2012 se vylíhla první dvě mláďata.'),
(85467, 3933, 'expozice', 'Dolní část zoo'),
(85468, 3933, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/areal/8660-dolni-cast-arealu'),
(85469, 3934, 'id', '51'),
(85470, 3934, 'latin_title', 'Catreus wallichii'),
(85471, 3934, 'trida', 'Ptáci (Aves)'),
(85472, 3934, 'vyhledavani', 'Bažant Wallichův'),
(85473, 3934, 'skupina', 'Hrabaví (Galliformes)'),
(85474, 3934, 'popisek', 'Samec i samice jsou hnědí, s tmavou kresbou, která je u samice hustší. Samec má chocholku na temeni hlavy, výraznou červenou lysinu na tvářích a ostré ostruhy na nohou. \r\n'),
(85475, 3934, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/Bazant_wallichuv__(Catreus_wallichii)_Z_oo_Plzen_4.5.2010_Ja.jpg'),
(85476, 3934, 'obrazek_popis', 'Foto: Jaroslav Vogeltanz,Zoo Plzen'),
(85477, 3934, 'kontinent', 'Asie'),
(85478, 3934, 'kontinent-detail', 'jižní svahy západního Himálaje'),
(85479, 3934, 'biotop', 'hory'),
(85480, 3934, 'biotop-detail', 'horské svahy do 3 000 m n. m. − často porosty trav a nízkých křovin udržované pravidelnou pastvou nebo kácením a vypalováním lesů'),
(85481, 3934, 'potrava', 'bezobratlí'),
(85482, 3934, 'potrava-detail', 'semena, výhonky, kořínky, bezobratlí'),
(85483, 3934, 'miry', 'délka těla − samice 61−67 cm, samec 90−112 cm, délka ocasu – samice 32−47 cm, samec 45−58 cm, hmotnost – samice 900−1 360 g, samec 1 250−1 800 g'),
(85484, 3934, 'rozmnozovani', 'Samice snáší 10−11 vajec, na kterých sedí 26 dní.'),
(85485, 3934, 'zajimavosti', 'Je to pospolitý druh, který mimo dobu hnízdění vytváří skupiny až 15 jedinců. Hnízdo je mělká jamka, umístěná na strmých svazích pod skalním převisem a dobře ukrytá v porostu. Samec se po dobu inkubace zdržuje poblíž hnízda a poté pomáhá samici s péčí o mláďata.'),
(85486, 3934, 'chov', 'Bažanty Wallichovy chováme od r. 2011.'),
(85487, 3934, 'expozice', 'Dolní část zoo'),
(85488, 3934, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/areal/8660-dolni-cast-arealu'),
(85489, 3935, 'id', '52'),
(85490, 3935, 'latin_title', 'Chrysolophus pictus'),
(85491, 3935, 'trida', 'Ptáci (Aves)'),
(85492, 3935, 'vyhledavani', 'Bažant zlatý'),
(85493, 3935, 'skupina', 'Hrabaví (Galliformes)'),
(85494, 3935, 'popisek', 'Patří k nejbarevnějším druhům bažantů. Samec má nápadnou zlatavou kápi, červenou spodní část těla a dlouhá ocasní pera. Samice je nevýrazně hnědavá.'),
(85495, 3935, 'obrazek', 'https://g.denik.cz/104/66/rtx2xoox.jpg'),
(85496, 3935, 'obrazek_popis', '\'\''),
(85497, 3935, 'kontinent', 'Asie'),
(85498, 3935, 'kontinent-detail', 'západní a střední Čína'),
(85499, 3935, 'biotop', 'tropický les'),
(85500, 3935, 'biotop-detail', 'horské svahy i údolí s hustými mladými lesy'),
(85501, 3935, 'potrava', 'bezobratlí'),
(85502, 3935, 'potrava-detail', 'listy, mladé výhonky, květy, členovci'),
(85503, 3935, 'miry', 'délka těla od 61 cm (samice) do 115 cm (samec), z toho až 79 cm tvoří ocas; hmotnost 550–875 g'),
(85504, 3935, 'rozmnozovani', 'Samice snáší 5–12 vajec, na kterých sedí 22 dny.'),
(85505, 3935, 'zajimavosti', 'Navzdory pestrému zbarvení samců uniká v přírodě pozornosti, protože se zdržuje v tmavých jalovcových porostech, kde splývá se stíny. Proto o jeho životě v přírodě máme jen málo údajů. Jako všichni bažanti hledá potravu na zemi, kde ji vyhrabává silným zobákem. Na noc vylétá hřadovat do korun stromů. Létá prudce a rychle, ale v nebezpečí se raději snaží utéct a skrýt se ve vegetaci.'),
(85506, 3935, 'chov', 'První bažanti zlatí přišli v r. 1999. V současné době chováme pár, který přišel v r. 2013 z plzeňské zoo.'),
(85507, 3935, 'expozice', 'Sečuán - pavilon ptáků z podhůří Himálaje'),
(85508, 3935, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/pavilony/1441-secuan-pavilon-ptaku-z-podhuri-himalaje'),
(85509, 3936, 'id', '59'),
(85510, 3936, 'latin_title', 'Branta canadensis'),
(85511, 3936, 'trida', 'Ptáci (Aves)'),
(85512, 3936, 'vyhledavani', 'Berneška kanadská'),
(85513, 3936, 'skupina', 'Vrubozobí (Anseriformes)'),
(85514, 3936, 'popisek', 'Je to statná husa s černou hlavou a krkem, šedobéžovým tělem, bílou spodinou těla a černým ocasem. Nohy jsou šedé. Typickým znakem je široká bílá uzdička.\r\n'),
(85515, 3936, 'obrazek', 'https://zoopraha.cz/images/lexikon/berneška_kanadská_1.jpg'),
(85516, 3936, 'obrazek_popis', 'Berneška kanadská, foto: archiv Zoo Praha'),
(85517, 3936, 'kontinent', 'Severní Amerika'),
(85518, 3936, 'kontinent-detail', 'Od severního Mexika po Aljašku, občas zalétá do severní Evropy, byla vysazena na Novém Zélandě a britských ostrovech.'),
(85519, 3936, 'biotop', 'travnaté území'),
(85520, 3936, 'biotop-detail', 'řeky, jezea, prérie'),
(85521, 3936, 'potrava', 'části rostlin'),
(85522, 3936, 'potrava-detail', 'vodní i suchozemské rostliny, semena, také obilí'),
(85523, 3936, 'miry', 'délka těla 43–93 cm, délka ocasu 12–17 cm, rozpětí křídel 122–183 cm, hmotnost 2–6,5 kg'),
(85524, 3936, 'rozmnozovani', 'Samice snáší 5-7 vajec, na kterých sedí 28-30 dní.'),
(85525, 3936, 'zajimavosti', 'Zdržují se poblíž stojatých nebo mírně tekoucích vod a silným zobákem oštipují části vodních i suchozemských rostlin. Bernešky žijí v párech, které se vytvářejí na zimovišti nebo při tahu a velmi často vydrží několik let nebo i celý život. Hnízdí na zemi, v podrostu kolem vody. Hnízdiště leží víc na severu jejich areálu a na zimu odlétají do jižnějších oblastí.  '),
(85526, 3936, 'chov', 'Vytváří několik poddruhů, z nichž v pražské zoo můžete vidět labradorský (B. c. canadensis), sdílející výběh s bizony. '),
(85527, 3936, 'expozice', 'Pláně'),
(85528, 3936, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/expozicni-celky/5810-plane'),
(85529, 3937, 'id', '60'),
(85530, 3937, 'latin_title', 'Branta ruficollis'),
(85531, 3937, 'trida', 'Ptáci (Aves)'),
(85532, 3937, 'vyhledavani', 'Berneška rudokrká'),
(85533, 3937, 'skupina', 'Vrubozobí (Anseriformes)'),
(85534, 3937, 'popisek', 'Patří k nejnápadněji zbarveným druhům hus. Přední část krku, hruď a tváře jsou rezavé, s bílým lemem. Bílý pruh se táhne od hlavy přes bohy na zadní část břicha. Hřbet, hlava a přední část břicha jsou uhlově černé. Má krátký černý zobák a tmavošedé nohy.\r\n'),
(85535, 3937, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/berneska_rudokrka__-_archiv_Zoo_Praha.jpg'),
(85536, 3937, 'obrazek_popis', 'Berneška rudokrká, foto: archiv Zoo Praha'),
(85537, 3937, 'kontinent', 'Evropa'),
(85538, 3937, 'kontinent-detail', 'Hnízdí v severní Eurasii, zimuje u Černého moře - protahuje přes Řecko a Bulharsko.'),
(85539, 3937, 'biotop', 'tundra'),
(85540, 3937, 'biotop-detail', 'tundra, step, řeky, jezera, zemědělská krajina'),
(85541, 3937, 'potrava', 'části rostlin'),
(85542, 3937, 'potrava-detail', 'zelené části trav a bylin, semena, obilí'),
(85543, 3937, 'miry', 'délka těla 43–45 cm, délka ocasu 10–11 cm, rozpětí křídel 116–135 cm, hmotnost 1–1,3 kg'),
(85544, 3937, 'rozmnozovani', 'Samice snáší 6–7 vajec do hnízda na zemi, inkubace trvá 23–25 dní.'),
(85545, 3937, 'zajimavosti', 'Hnízdí v koloniích, často v blízkosti hnízd sovic sněžních nebo sokolů, což zvyšuje bezpečnost pro mláďata. '),
(85546, 3937, 'chov', 'Bernešky rudokrké chováme od r. 1998. První mláďata se vylíhla a byla odchována v r. 2003.'),
(85547, 3937, 'expozice', 'Ptačí mokřady'),
(85548, 3937, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/expozicni-celky/1685-ptaci-mokrady'),
(85549, 3938, 'id', '61'),
(85550, 3938, 'latin_title', 'Ahaetulla prasina'),
(85551, 3938, 'trida', 'Plazi (Reptilia)'),
(85552, 3938, 'vyhledavani', 'Bičovka stromová'),
(85553, 3938, 'skupina', 'Šupinatí (Squamata)'),
(85554, 3938, 'popisek', 'Má dlouhé bičovité tělo a štíhlou špičatou hlavu. Velkýma očima s vodorovnou zorničkou dobře odhadne vzdálenost. Typický je protáhlý nos. Zbarvení může být zářivě zelené, olivové až šedozelené.\r\n'),
(85555, 3938, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/bicovka_sromova_-_Tereza_Mrhalkova.jpg'),
(85556, 3938, 'obrazek_popis', 'Bičovka stromová, foto: Tereza Mrhálková, Zoo Praha'),
(85557, 3938, 'kontinent', 'Asie'),
(85558, 3938, 'kontinent-detail', 'jižní a jihovýchodní Asie'),
(85559, 3938, 'biotop', 'tropický les'),
(85560, 3938, 'biotop-detail', 'tropické lesy, křoviny, parky, zahrady'),
(85561, 3938, 'potrava', 'živá kořist'),
(85562, 3938, 'potrava-detail', 'ještěrky, žáby i jiní malí obratlovci'),
(85563, 3938, 'miry', 'délka těla až 150 cm, délka ocasu až 50 cm, hmotnost v průměru 300 g'),
(85564, 3938, 'rozmnozovani', 'vejcoživorodá; počet mláďat 4–10'),
(85565, 3938, 'zajimavosti', 'Jako všechny bičovky se dokáže dokonale proplést mezi větvemi, takže se doslova ztrácí ve změti popínavých rostlin a listů. Většinu času se zdržuje v korunách stromů nebo v keřích, často však loví na zemi. Je aktivní ve dne. Má jedové zuby umístěné v zadní části čelistí. Kořist sevře a drží ji, dokud jed nezačne působit.'),
(85566, 3938, 'chov', 'Poměrně velké množství bičovek sice prošlo pražskou zoo již v 50. letech 20. století, jednalo se však o tranzitní zvířata. Návštěvníci měli možnost se s nimi seznámit až v teráriu v pavilonu Indonéská džungle, kde se první samice objevila v r. 2004. V letech 2008−2011 nastala v chovu krátká přestávka, ale od té doby nejen že bičovky, chováme, ale dokonce i rozmnožujeme, a to od. r. 2011.'),
(85567, 3938, 'expozice', 'Pavilon Indonéská džungle '),
(85568, 3938, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/pavilony/5828-indoneska-dzungle'),
(85569, 3939, 'id', '62'),
(85570, 3939, 'latin_title', 'Acticits binturong'),
(85571, 3939, 'trida', 'Savci (Mammalia)'),
(85572, 3939, 'vyhledavani', 'Binturong'),
(85573, 3939, 'skupina', 'Šelmy (Carnivora)'),
(85574, 3939, 'popisek', 'Binturong je největší cibetkovitá šelma, samice jsou větší než samci. Typické jsou dlouhé chomáče srsti na boltcích a zářivě hnědé až cihlové oči. Ocas je ovíjivý. Poddruh A. b. whitei, který v současné době chováme, je oproti kontinentálnímu poddruhu menší, má černou srst a výraznější bílé lemy ušních boltců.\r\n'),
(85575, 3939, 'obrazek', 'https://zoopraha.cz/images/lexikon/binturong_8.jpg'),
(85576, 3939, 'obrazek_popis', 'Binturong, foto: Archiv Zoo Praha'),
(85577, 3939, 'kontinent', 'Asie'),
(85578, 3939, 'kontinent-detail', 'jihovýchodní Asie včetně některých indonéských ostrovů, poddruh A. b. whitei žije na Sumatře.'),
(85579, 3939, 'biotop', 'tropický les'),
(85580, 3939, 'biotop-detail', 'primární i sekundární tropické lesy'),
(85581, 3939, 'potrava', 'rostliny i živočichové'),
(85582, 3939, 'potrava-detail', 'hlavně plody, případně menší živočichové, ptačí vejce, zdechliny'),
(85583, 3939, 'miry', 'délka těla 61–96 cm, délka ocasu 61–96 cm, hmotnost 20–25 kg'),
(85584, 3939, 'rozmnozovani', 'Samice je březí 90–92 dny, rodí 2–3 (výjimečně 4) mláďata.'),
(85585, 3939, 'zajimavosti', 'Zdržuje se v korunách stromů, kde dokáže obratně šplhat, ve větvích i spí a odchovává mláďata. Dovede také výborně plavat. Je aktivní za šera, výjimečně i během dne. Nejraději má sladké plody a patří k živočichům, kteří se významně podílejí na šíření semen různých stromů. Žije nejčastěji samotářsky; pokud člověk v přírodě narazí na skupinku, jedná se obvykle o samici s odrostlými mláďaty.'),
(85586, 3939, 'chov', 'Od r. 1959 obývali binturongové (s různě dlouhými přestávkami) Pavilon vzácných živočichů (dnes Afrika zblízka) až do r. 1994. Poté nastala pauza a nová zvířata přišla již do pavilonu Indonéská džungle. První byla v r. 2005 samička, která pocházela z Hanoje, poté přibyl v r. 2006 pár a v r. 2007 další samice. Samec ze zmíněného páru žije dosud – v r. 2013 odcestoval z Prahy do jiné zoo, a to v souvislosti s příchodem nového poddruhu. První úspěšný odchov se podařil v r. 2009, kdy se narodila dvojčata, a od té doby bylo odchováno dalších devět mláďat. Začátkem roku 2014 se v pavilonu začal zabydlovat pár binturongů palawanských (A. b. whitei).'),
