(86110, 3966, 'latin_title', 'Marmaronetta angustirostris'),
(86111, 3966, 'trida', 'Ptáci (Aves)'),
(86112, 3966, 'vyhledavani', 'Čírka úzkozobá'),
(86113, 3966, 'skupina', 'Vrubozobí (Anseriformes)'),
(86114, 3966, 'popisek', 'Má celkově hnědé až hnědošedé zbarvení s béžovým skvrněním. Tenký zobák je černý. Na temeni má malou chocholku.\r\n'),
(86115, 3966, 'obrazek', 'https://zoopraha.cz/images/lexikon/cirka_uzkozoba.jpg'),
(86116, 3966, 'obrazek_popis', 'Čírka úzkozobá, foto: archiv Zoo Praha'),
(86117, 3966, 'kontinent', 'Evropa'),
(86118, 3966, 'kontinent-detail', 'Vytváří tři oddělené populace – jedna hnízdí v západním Středomoří a zimuje v severní a západní Africe, druhá hnízdí ve východním Středomoří a zimuje v Egyptě a třetí hnízdí v západní a jižní Asii a zimuje v Íránu, Pákistánu a Indii.'),
(86119, 3966, 'biotop', 'sladké vody'),
(86120, 3966, 'biotop-detail', 'zarostlé mokřady, sladkovodní a brakické mělčiny'),
(86121, 3966, 'potrava', 'bezobratlí'),
(86122, 3966, 'potrava-detail', 'části vodních rostlin, vodní bezobratlí'),
(86123, 3966, 'miry', 'délka 39−48 cm, hmotnost 450−600 g'),
(86124, 3966, 'rozmnozovani', 'Samice snáší 7−12 vajec, na kterých sedí sama 25−27 dní.'),
(86125, 3966, 'zajimavosti', 'Je příbuzná víc polákům než ostatním čírkám a tomu odpovídá i zvyk potápět se pro potravu pod hladinu. Na zimovišti vytvářejí čírky sezonní páry, které pak společně vyhledávají místo pro hnízdo. '),
(86126, 3966, 'chov', 'Chováme je od roku 1980, od roku 2006 je i rozmnožujeme.'),
