(86291, 3975, 'trida', 'Savci (Mammalia)'),
(86292, 3975, 'vyhledavani', 'Dikobraz srstnatonosý'),
(86293, 3975, 'skupina', 'Hlodavci (Rodentia)'),
(86294, 3975, 'popisek', 'Je to představitel „typických“ dikobrazů. Má velkou hlavu s tupým čenichem a hřbet, ocas a boky pokryté různě dlouhými bodlinami a ostny.\r\n'),
(86295, 3975, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/dikobraz_srstnatonosy_IMG_7616.jpg'),
(86296, 3975, 'obrazek_popis', 'Dikobraz srstnatonosý, foto: archiv Zoo Praha'),
