(86318, 3976, 'kontinent-detail', 'severní Austrálie, jižní Nová Guinea'),
(86319, 3976, 'biotop', 'sladké vody'),
(86320, 3976, 'biotop-detail', 'mokřady, mělké vody'),
(86321, 3976, 'potrava', 'živá kořist'),
(86322, 3976, 'potrava-detail', 'vodní živočichové'),
(86323, 3976, 'miry', 'délka krunýře: 30–34 cm, hmotnost 3 kg'),
(86324, 3976, 'rozmnozovani', 'počet vajec 10–20, inkubace 6–7 měsíců'),
(86325, 3976, 'zajimavosti', 'Dlouhokrčka tráví většinu života v hlubokém bahně pobřežních mokřadů, které obývá. Je takřka výhradně masožravá, živí se rybami, plži, červy a jinými bahenními bezobratlými. Samice klade koncem období dešťů vajíčka, z nichž se líhnou tmavě skvrnitá mláďata.'),
(86326, 3976, 'chov', 'Pár dlouhokrček přišel do pavilonu Indonéská džungle v roce 2004. První mláďata byla odchována v roce 2009.'),
(86327, 3976, 'expozice', 'Pavilon Indonéská džungle '),
