(86329, 3977, 'id', '250'),
(86330, 3977, 'latin_title', 'Dracaena guianensis'),
(86331, 3977, 'trida', 'Plazi (Reptilia)'),
(86332, 3977, 'vyhledavani', 'Dracéna krokodýlovitá'),
(86333, 3977, 'skupina', 'Šupinatí (Squamata)'),
(86334, 3977, 'popisek', 'Je to statný ještěr s velkou hlavou a silným krkem. Tělo je olivově zelené, hlava světle oranžová. Velké oči jsou černé. Na hřbetě se táhnou řady šupin s nizkými hrbolky, kterými trochu připomíná kůži krokodýlů.\r\n'),
(86335, 3977, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/dracana_guyansk__-_Tom_A_Adamec.jpg'),
(86336, 3977, 'obrazek_popis', 'Foto: Tomáš Adamec, Zoo Praha'),
(86337, 3977, 'kontinent', 'Jižní Amerika'),
