(86737, 3997, 'kontinent', 'Afrika'),
(86738, 3997, 'kontinent-detail', 'severní Afrika - jihovýchodní Maroko, jižní Alžírsko, Tunisko, Libye'),
(86739, 3997, 'biotop', 'kamenité stepi a polopouště'),
(86740, 3997, 'biotop-detail', 'skalnaté pouště'),
