(87653, 4043, 'skupina', 'Lichokopytníci (Perissodactyla)'),
(87654, 4043, 'popisek', '\r\nMá světle rezavé až hnědé zbarvení srsti, břicho, nohy a přední strana krku jsou bílé. Středem hřbetu se táhne tmavý pruh, hříva je krátká, vztyčená a tmavá.\r\n\r\n \r\n'),
(87655, 4043, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/_TTT1203.jpg'),
(87656, 4043, 'obrazek_popis', 'Foto: Tomáš Adamec, Zoo Praha'),
(87657, 4043, 'kontinent', 'Asie'),
(87658, 4043, 'kontinent-detail', 'Tibetská náhorní plošina v polohách 2 700−5 400 m n. m. – Čína, také Pákistán, Indie, Nepál a zřejmě i Bhútán; kiang východní se vyskytuje v čínských provinciích Quinghai a jihozápadní Xinjiang.'),
(87659, 4043, 'biotop', 'kamenité stepi a polopouště'),
(87660, 4043, 'biotop-detail', 'stepi, náhorní plošiny'),
(87661, 4043, 'potrava', 'části rostlin'),
