INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(87943, 4057, 'miry', 'délka těla 17–40 cm, délka ocasu 8–20 cm, hmotnost 0,6–1 kg'),
(87944, 4057, 'rozmnozovani', 'počet vajec 3–9, inkubace 124–142 dny'),
(87945, 4057, 'zajimavosti', 'Korovci jsou jedněmi z mála známých jedovatých ještěrů na světě. Ve dvou druzích se vyskytují výhradně na jihozápadě Severní Ameriky a ve Střední Americe. Jedové žlázy jim ústí v zadní části dolní čelisti a jed proudí rýhami na povrchu několika párů zvětšených (až 5 mm dlouhých) špičatých zubů, jimiž kořist silně stisknou, aby ji ochromili. Jsou to poměrně pomalí ještěři, kteří se živí mláďaty ptáků hnízdících na zemi, jinými menšími ještěry, želvami, hlodavci a vejci. Naráz zkonzumují potravu odpovídající až třetině hmotnosti. Dovedou obratně šplhat. Jsou aktivní ve dne, noc tráví v podzemních norách, které si sami hrabou. Samice klade vajíčka, o která nijak nepečuje. Pokud dojde ke snůšce koncem léta, přečkávají vejce zimu a mláďata se líhnou až po 10 měsících'),
(87946, 4057, 'chov', 'Korovci byli chováni v letech 1958 až 1989 a poté od roku 1994 dodnes.'),
(87947, 4057, 'expozice', 'Terárium'),
