(88491, 4085, 'trida', 'Savci (Mammalia)'),
(88492, 4085, 'vyhledavani', 'Lemur běločelý'),
(88493, 4085, 'skupina', 'Primáti (Primates)'),
(88494, 4085, 'popisek', 'Bíle zbarvená hlava je typickým znakem samců, samice mají hlavu šedohnědou. Rozdíl mezi oběma pohlavími je přitom patný již na několikatýdenních mláďatech.\r\n'),
(88495, 4085, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/Lemur_buloael_.jpg'),
(88496, 4085, 'obrazek_popis', 'Lemur běločelý, foto: archiv Zoo Praha'),
(88497, 4085, 'kontinent', 'Afrika'),
(88498, 4085, 'kontinent-detail', 'Madagaskar'),
