(89076, 4114, 'obrazek_popis', 'Nandu Darwinův, foto: archiv Zoo Praha'),
(89077, 4114, 'kontinent', 'Jižní Amerika'),
(89078, 4114, 'kontinent-detail', 'Peru, Bolívie, Chile, Argentina'),
(89079, 4114, 'biotop', 'hory'),
(89080, 4114, 'biotop-detail', 'křoviny, horské planiny'),
