(89794, 4150, 'popisek', '\r\nMalý druh s celkově šedým zbarvením a huňatým ocasem.\r\n\r\n \r\n'),
(89795, 4150, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/Plch_savanovy.jpg'),
