INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(90384, 4179, 'rozmnozovani', 'Samice snáší 1 vejce, na kterém sedí oba rodiče 48–54 dní.'),
(90385, 4179, 'zajimavosti', 'Úzký zobák dokáže prostrčit i do malých štěrbin mezi kostmi. Dostane se tak ke kouskům masa, které jsou pro větší supy nedostupné a na mršinách po nich zbydou. Často zalétává i k lidským obydlím, kde sbírá zbytky. '),
(90386, 4179, 'chov', 'Chovali jsme je v letech 1954−1965, nově jsme chov obnovili v roce 2012.'),
(90387, 4179, 'expozice', 'Dravci'),
(90388, 4179, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/expozicni-celky/5808-dravci'),
(90389, 4180, 'id', '278'),
(90390, 4180, 'latin_title', 'Neophron percnopterus'),
(90391, 4180, 'trida', 'Ptáci (Aves)'),
(90392, 4180, 'vyhledavani', 'Sup mrchožravý'),
(90393, 4180, 'skupina', 'Dravci (Falconiformes)'),
(90394, 4180, 'popisek', '\r\nV dospělosti je nezaměnitelný. Má hlavu a krk ze všech supů nejméně lysé a je z nich nejmenší. Především však má převážně bílé peří, tmavé letky a žlutou až oranžovou lysou kůži na hlavě. Mladí ptáci jsou hnědaví.'),
(90395, 4180, 'obrazek', 'https://zoopraha.cz/images/lexikon/Sup_mrchožravý_5.jpg'),
(90396, 4180, 'obrazek_popis', 'Sup mrchožravý, foto: archiv Zoo Praha'),
(90397, 4180, 'kontinent', 'Evropa'),
(90398, 4180, 'kontinent-detail', 'V několika poddruzích jižní Evropa, na východ do Střední Asie a severozápadní Indie, dále severní Afrika po Tanzanii, Arábie. Část populací migruje na zimu do subsaharské Afriky'),
(90399, 4180, 'biotop', 'hory'),
(90400, 4180, 'biotop-detail', 'otevřená suchá krajina v nižších polohách se skalními útvary'),
(90401, 4180, 'potrava', 'zdechliny'),
(90402, 4180, 'potrava-detail', 'zdechliny, pštrosí vejce, drobní obratlovci, občas přezrálé plody'),
(90403, 4180, 'miry', 'délka těla 36–44 cm, délka ocasu 22–26 cm, rozpětí křídel 155–170 cm, hmotnost 1,6–2,2 kg'),
(90404, 4180, 'rozmnozovani', 'Snáší 1–2 vejce, sedí na nich 40–44 dny.'),
(90405, 4180, 'zajimavosti', 'Na rozdíl od ostatních supů má poměrně slabý zobák. Potravu především sbírá, proto má lysé pouze tváře a zbývající část hlavy a krku jsou opeřené. Je dobrým letcem – při pátrání po potravě uletí denně až 80 km. Do svého jídelníčku začleňuje kromě zdechlin také vejce ptáků, obzvláště pštrosů. Jejich tvrdou skořápku rozbíjí pomocí kamene, který drží v zobáku.'),
(90406, 4180, 'chov', 'První supi mrchožraví přišli v roce 1936. Poté jsme je chovali s krátkou přestávkou ve 40. letech až do roku 1973, načež tento druh na dlouhých dvacet let ze zoo zmizel. Až v roce 1993 přišel z děčínské zoo samec, který žije v Praze dosud. Společně se samicí, která dorazila v roce 1998 z Rigy a pocházela z volné přírody, se stali rodiči prvního odchovaného mláděte. Bylo to v roce 2000 a sameček žije v naší zoo i dnes. Od té doby se podařilo odchovat dalších 12 mláďat včetně trojčat, která se vylíhla v červnu 2014. Patříme tak k nejúspěšnějším chovatelům tohoto ohroženého dravce, proto od roku 2012 vedeme EEP.\r\nOchrana\r\nSup mrchožravý je nejmenším a zároveň nejohroženějším evropským supem. Na Starém kontinentu je totiž nezákonně střílen a tráven a nenachází zde dostatek vhodné potravy. Mezi nejohroženější populace v Evropě patří balkánská, kterou tvoří méně než stovka dospělých párů a na Červeném seznamu IUCN je řazena do kategorie „kriticky ohrožený“. Proto se Zoo Praha zasazuje o její ochranu – mimo jiné i s využitím příspěvků z konta Pomáháme jim přežít. '),
(90407, 4180, 'expozice', 'Dravci'),
