(90592, 4190, 'vyhledavani', 'Tana severní'),
(90593, 4190, 'skupina', 'Tany (Scadentia)'),
(90594, 4190, 'popisek', '\r\nMá štíhlé tělo, protáhlý čenich, pětiprsté končetiny s ostrými drápky a dlouhý, huňatý ocas. Zbarvení je olivově zelenošedé, zespodu světlejší. Samci jsou oproti samicím větší a mají kolem očí světlou srst.\r\n\r\n \r\n'),
(90595, 4190, 'obrazek', 'https://zoopraha.cz/images/lexikon/tana_severní_4.jpg'),
(90596, 4190, 'obrazek_popis', 'Tana severní, foto: archiv Zoo Praha'),
(90597, 4190, 'kontinent', 'Asie'),
(90598, 4190, 'kontinent-detail', 'jihovýchodní Asie'),
