INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(62980, 2939, 'popisek', 'Patří do skupiny tzv. ušatých bažantů, jejichž typickým znakem jsou prodloužená pírka po stranách hlavy. Vytváří několik poddruhů, u všech se samec od samice téměř neliší.\r\n'),
(62981, 2939, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/BaIant_tibetsk_.jpg'),
(62982, 2939, 'obrazek_popis', 'Bažant tibetský, foto: Tereza Mrhálková, Zoo Praha'),
(62983, 2939, 'kontinent', 'Asie'),
(62984, 2939, 'kontinent-detail', 'jižní Asie – od Tibetu a severní Indie po jižní a střední Čínu'),
(62985, 2939, 'biotop', 'jehličnatý les'),
(62986, 2939, 'biotop-detail', 'jehličnaté i smíšené lesy a březové a pěnišníkové háje mezi 3 500 a 4 300 m n. m.'),
(62987, 2939, 'potrava', 'bezobratlí'),
(62988, 2939, 'potrava-detail', 'semena, výhonky a další části rostlin, bezobratlí'),
(62989, 2939, 'miry', 'délka těla 86–96 cm, délka ocasu 46–58 cm, hmotnost samců 2,3–2,75 kg, samic 1,4–2 kg'),
(62990, 2939, 'rozmnozovani', 'Samice snáší 4–9 vajec, na kterých sedí 24 dny – údaje z chovů.'),
(62991, 2939, 'zajimavosti', 'Silným zobákem doslova překopává půdu, aby získal potravu. Většinu roku tráví ve skupinkách až o třiceti ptácích, v době rozmnožování žije v párech. Je velmi otužilý a běžně se zdržuje i na zasněžených svazích. Dokáže úsporně přeletovat nízko nad sněhem, aby se dostal k dalšímu zdroji potravy. Při klouzavém letu využívá široký ocas.'),
(62992, 2939, 'chov', 'První bažant tibetský přišel do Prahy z „Durrellovy“ zoo na Jersey v r. 1977, žil v zoo do r. 1992 a pak odešel do Ostravy. Mezitím přicházeli do zoo další jedinci, mimo jiné v r. 1990 pár, který v r. 1992 odchoval historicky první mláďata.\r\nZimu tráví v zázemí zoo, mimo návštěvnické prostory.'),
(62993, 2939, 'expozice', 'Dolní část zoo'),
(62994, 2939, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/areal/8660-dolni-cast-arealu'),
(62995, 2940, 'id', '53'),
(62996, 2940, 'latin_title', 'Phasianus colchicus zarudnyi'),
(62997, 2940, 'trida', 'Ptáci (Aves)'),
(62998, 2940, 'skupina', 'Hrabaví (Galliformes)'),
(62999, 2940, 'popisek', 'Vzhledem se podobá nominátnímu poddruhu (bažantu kolchickému), ale nemá bílý obojek. Samec je pestrý, má hnědé tělo s tmavou kresbou, kovově lesklou zelenou hlavu a prodloužená ocasní pera. Samice je nevýrazně hnědá. Ona mají červenou lysinu na tvářích.\r\n'),
(63000, 2940, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/bazant_turkmensky_-_Tomas_Adamec.jpg'),
(63001, 2940, 'obrazek_popis', 'Bažant turkménský, foto: Tomáš Adamec, Zoo Praha'),
(63002, 2940, 'kontinent', 'Asie'),
(63003, 2940, 'kontinent-detail', 'Turkmenistán − malé území v údolích středního toku řeky Amudarji'),
(63004, 2940, 'biotop', 'travnaté území'),
(63005, 2940, 'biotop-detail', 'otevřená travnatá krajina s roztroušenými remízky a křovinami, kulturní krajina'),
(63006, 2940, 'potrava', 'rostliny i živočichové'),
(63007, 2940, 'potrava-detail', 'bezobratlí, semena, podzemní části rostlin, výhonky'),
(63008, 2940, 'miry', 'délka těla 42,5−53,5 cm, délka křídla 23,5−25,8 cm, hmotnost 900−1 200 g'),
(63009, 2940, 'rozmnozovani', 'Samice snáší do důlku na zemi 7−15 vajec, na kterých sedí sama 23−27 dní.'),
(63010, 2940, 'zajimavosti', 'Způsobem života se neliší od bažanta kolchického, který se vyskytuje jako nepůvodní druh i u nás. Samec je polygamní, v době toku láká samice hlasitým volání a zvláštním „tancem“ spojeným s třepetáním křídly a náznakovým zobáním.'),
(63011, 2940, 'chov', 'Poddruh chováme od r. 2011, v r. 2012 se vylíhla první dvě mláďata.'),
(63012, 2940, 'expozice', 'Dolní část zoo'),
(63013, 2940, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/areal/8660-dolni-cast-arealu'),
(63014, 2941, 'id', '51'),
(63015, 2941, 'latin_title', 'Catreus wallichii'),
(63016, 2941, 'trida', 'Ptáci (Aves)'),
(63017, 2941, 'skupina', 'Hrabaví (Galliformes)'),
(63018, 2941, 'popisek', 'Samec i samice jsou hnědí, s tmavou kresbou, která je u samice hustší. Samec má chocholku na temeni hlavy, výraznou červenou lysinu na tvářích a ostré ostruhy na nohou. \r\n'),
(63019, 2941, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/Bazant_wallichuv__(Catreus_wallichii)_Z_oo_Plzen_4.5.2010_Ja.jpg'),
(63020, 2941, 'obrazek_popis', 'Foto: Jaroslav Vogeltanz,Zoo Plzen'),
(63021, 2941, 'kontinent', 'Asie'),
(63022, 2941, 'kontinent-detail', 'jižní svahy západního Himálaje'),
(63023, 2941, 'biotop', 'hory'),
(63024, 2941, 'biotop-detail', 'horské svahy do 3 000 m n. m. − často porosty trav a nízkých křovin udržované pravidelnou pastvou nebo kácením a vypalováním lesů'),
(63025, 2941, 'potrava', 'bezobratlí'),
(63026, 2941, 'potrava-detail', 'semena, výhonky, kořínky, bezobratlí'),
(63027, 2941, 'miry', 'délka těla − samice 61−67 cm, samec 90−112 cm, délka ocasu – samice 32−47 cm, samec 45−58 cm, hmotnost – samice 900−1 360 g, samec 1 250−1 800 g'),
(63028, 2941, 'rozmnozovani', 'Samice snáší 10−11 vajec, na kterých sedí 26 dní.'),
(63029, 2941, 'zajimavosti', 'Je to pospolitý druh, který mimo dobu hnízdění vytváří skupiny až 15 jedinců. Hnízdo je mělká jamka, umístěná na strmých svazích pod skalním převisem a dobře ukrytá v porostu. Samec se po dobu inkubace zdržuje poblíž hnízda a poté pomáhá samici s péčí o mláďata.'),
(63030, 2941, 'chov', 'Bažanty Wallichovy chováme od r. 2011.'),
(63031, 2941, 'expozice', 'Dolní část zoo'),
(63032, 2941, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/areal/8660-dolni-cast-arealu'),
(63033, 2942, 'id', '52'),
(63034, 2942, 'latin_title', 'Chrysolophus pictus'),
(63035, 2942, 'trida', 'Ptáci (Aves)'),
(63036, 2942, 'skupina', 'Hrabaví (Galliformes)'),
(63037, 2942, 'popisek', 'Patří k nejbarevnějším druhům bažantů. Samec má nápadnou zlatavou kápi, červenou spodní část těla a dlouhá ocasní pera. Samice je nevýrazně hnědavá.'),
(63038, 2942, 'obrazek', 'https://g.denik.cz/104/66/rtx2xoox.jpg'),
(63039, 2942, 'obrazek_popis', '\'\''),
(63040, 2942, 'kontinent', 'Asie'),
(63041, 2942, 'kontinent-detail', 'západní a střední Čína'),
(63042, 2942, 'biotop', 'tropický les'),
(63043, 2942, 'biotop-detail', 'horské svahy i údolí s hustými mladými lesy'),
(63044, 2942, 'potrava', 'bezobratlí'),
(63045, 2942, 'potrava-detail', 'listy, mladé výhonky, květy, členovci'),
(63046, 2942, 'miry', 'délka těla od 61 cm (samice) do 115 cm (samec), z toho až 79 cm tvoří ocas; hmotnost 550–875 g'),
(63047, 2942, 'rozmnozovani', 'Samice snáší 5–12 vajec, na kterých sedí 22 dny.'),
(63048, 2942, 'zajimavosti', 'Navzdory pestrému zbarvení samců uniká v přírodě pozornosti, protože se zdržuje v tmavých jalovcových porostech, kde splývá se stíny. Proto o jeho životě v přírodě máme jen málo údajů. Jako všichni bažanti hledá potravu na zemi, kde ji vyhrabává silným zobákem. Na noc vylétá hřadovat do korun stromů. Létá prudce a rychle, ale v nebezpečí se raději snaží utéct a skrýt se ve vegetaci.'),
(63049, 2942, 'chov', 'První bažanti zlatí přišli v r. 1999. V současné době chováme pár, který přišel v r. 2013 z plzeňské zoo.'),
(63050, 2942, 'expozice', 'Sečuán - pavilon ptáků z podhůří Himálaje'),
(63051, 2942, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/pavilony/1441-secuan-pavilon-ptaku-z-podhuri-himalaje'),
(63052, 2943, 'id', '59'),
(63053, 2943, 'latin_title', 'Branta canadensis'),
(63054, 2943, 'trida', 'Ptáci (Aves)'),
(63055, 2943, 'skupina', 'Vrubozobí (Anseriformes)'),
(63056, 2943, 'popisek', 'Je to statná husa s černou hlavou a krkem, šedobéžovým tělem, bílou spodinou těla a černým ocasem. Nohy jsou šedé. Typickým znakem je široká bílá uzdička.\r\n'),
(63057, 2943, 'obrazek', 'https://zoopraha.cz/images/lexikon/berneška_kanadská_1.jpg'),
(63058, 2943, 'obrazek_popis', 'Berneška kanadská, foto: archiv Zoo Praha'),
(63059, 2943, 'kontinent', 'Severní Amerika'),
(63060, 2943, 'kontinent-detail', 'Od severního Mexika po Aljašku, občas zalétá do severní Evropy, byla vysazena na Novém Zélandě a britských ostrovech.'),
(63061, 2943, 'biotop', 'travnaté území'),
(63062, 2943, 'biotop-detail', 'řeky, jezea, prérie'),
(63063, 2943, 'potrava', 'části rostlin'),
(63064, 2943, 'potrava-detail', 'vodní i suchozemské rostliny, semena, také obilí'),
(63065, 2943, 'miry', 'délka těla 43–93 cm, délka ocasu 12–17 cm, rozpětí křídel 122–183 cm, hmotnost 2–6,5 kg'),
(63066, 2943, 'rozmnozovani', 'Samice snáší 5-7 vajec, na kterých sedí 28-30 dní.'),
(63067, 2943, 'zajimavosti', 'Zdržují se poblíž stojatých nebo mírně tekoucích vod a silným zobákem oštipují části vodních i suchozemských rostlin. Bernešky žijí v párech, které se vytvářejí na zimovišti nebo při tahu a velmi často vydrží několik let nebo i celý život. Hnízdí na zemi, v podrostu kolem vody. Hnízdiště leží víc na severu jejich areálu a na zimu odlétají do jižnějších oblastí.  '),
(63068, 2943, 'chov', 'Vytváří několik poddruhů, z nichž v pražské zoo můžete vidět labradorský (B. c. canadensis), sdílející výběh s bizony. '),
(63069, 2943, 'expozice', 'Pláně'),
(63070, 2943, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/expozicni-celky/5810-plane'),
(63071, 2944, 'id', '60'),
(63072, 2944, 'latin_title', 'Branta ruficollis'),
(63073, 2944, 'trida', 'Ptáci (Aves)'),
(63074, 2944, 'skupina', 'Vrubozobí (Anseriformes)'),
(63075, 2944, 'popisek', 'Patří k nejnápadněji zbarveným druhům hus. Přední část krku, hruď a tváře jsou rezavé, s bílým lemem. Bílý pruh se táhne od hlavy přes bohy na zadní část břicha. Hřbet, hlava a přední část břicha jsou uhlově černé. Má krátký černý zobák a tmavošedé nohy.\r\n'),
(63076, 2944, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/berneska_rudokrka__-_archiv_Zoo_Praha.jpg'),
