(63765, 2980, 'biotop-detail', 'savana, buš, řídké lesy'),
(63766, 2980, 'potrava', 'části rostlin'),
(63767, 2980, 'potrava-detail', 'výhonky, mladé listy, pupeny, hlízy, plody'),
