(63831, 2984, 'id', '250'),
(63832, 2984, 'latin_title', 'Dracaena guianensis'),
(63833, 2984, 'trida', 'Plazi (Reptilia)'),
(63834, 2984, 'skupina', 'Šupinatí (Squamata)'),
(63835, 2984, 'popisek', 'Je to statný ještěr s velkou hlavou a silným krkem. Tělo je olivově zelené, hlava světle oranžová. Velké oči jsou černé. Na hřbetě se táhnou řady šupin s nizkými hrbolky, kterými trochu připomíná kůži krokodýlů.\r\n'),
(63836, 2984, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/dracana_guyansk__-_Tom_A_Adamec.jpg'),
(63837, 2984, 'obrazek_popis', 'Foto: Tomáš Adamec, Zoo Praha'),
(63838, 2984, 'kontinent', 'Jižní Amerika'),
(63839, 2984, 'kontinent-detail', 'tropická Jižní Amerika'),
(63840, 2984, 'biotop', 'tropický les'),
