(63879, 2986, 'biotop-detail', 'podrost vlhkých listnatých, vždyzelených lesů, v nadmořské výšce od 250 do 1 830 m'),
(63880, 2986, 'potrava', 'plody'),
(63881, 2986, 'potrava-detail', 'hmyz, červi a jiní bezobratlí, také plody'),
