INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(63991, 2992, 'kontinent-detail', '\'\''),
(63992, 2992, 'biotop', 'travnaté území'),
(63993, 2992, 'biotop-detail', 'křovinatá step'),
(63994, 2992, 'potrava', 'plody'),
(63995, 2992, 'potrava-detail', 'plody, výhonky, bezobratlí'),
(63996, 2992, 'miry', 'délka těla 1,5–1,9 m, ocas nemá, hmotnost: 30–45 kg'),
(63997, 2992, 'rozmnozovani', 'počet vajec 5–15, inkubace 56 dní'),
(63998, 2992, 'zajimavosti', 'O potomky se u emuů stará především otec. Sám sedí necelé dva měsíce na vejcích a po celou dobu se postí. Pruhovaná mláďata neopustí ještě nejméně další půlrok. '),
(63999, 2992, 'chov', 'Již v letech 1933−1934 žili v zoo samci Pepík a poté Pepík II. První přišel z cirkusu Kludský, druhý přes firmu Hagenbeck. Záznamy z let 1946−1974 bohužel chybějí, ale poté je zřejmé, že chov emuů trval nepřetržitě až dodnes.'),
(64000, 2992, 'expozice', 'Horní část zoo'),
(64001, 2992, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/areal/8661-horni-cast-arealu'),
(64002, 2993, 'id', '107'),
(64003, 2993, 'latin_title', 'Vulpes zerda'),
(64004, 2993, 'trida', 'Savci (Mammalia)'),
(64005, 2993, 'skupina', 'Šelmy (Carnivora)'),
(64006, 2993, 'popisek', 'Drobná šelma s jemnou, pískově zbarvenou srstí, velkými ušními boltci a huňatým ocasem. Samec a samice vypadají stejně.\r\n'),
(64007, 2993, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/fenek_1.jpg'),
(64008, 2993, 'obrazek_popis', 'Fenek, foto: Tereza Mrhálková, Zoo Praha'),
(64009, 2993, 'kontinent', 'Afrika'),
(64010, 2993, 'kontinent-detail', 'Afrika, Arábie'),
(64011, 2993, 'biotop', 'poušť a polopoušť'),
(64012, 2993, 'biotop-detail', 'písčité pouště'),
(64013, 2993, 'potrava', 'živá kořist'),
(64014, 2993, 'potrava-detail', 'drobní živočichové'),
(64015, 2993, 'miry', 'Délka těla 36–40 cm, délka ocasu 18–30 cm, výška v kohoutku 18–22 cm, hmotnost 1–1,5 kg'),
(64016, 2993, 'rozmnozovani', 'březost 51 den, počet mláďat 2–5'),
(64017, 2993, 'zajimavosti', 'Světlá barva srsti je nejen maskuje, ale také účinně odráží sluneční paprsky. Tlapky jsou zespodu osrstěné, aby se nebořily do sypkého písku, a nápadné ušní boltce slouží jako ochlazovací výměník tepla a uvnitř jsou porostlé dlouhými chlupy, které při prudkých bouřích brání písku vniknout do zvukovodu. Díky dlouhým boltcům fenek rovněž slyší každé šustnutí a při nočním lovu mu neunikne žádný větší hmyz.'),
(64018, 2993, 'chov', 'Fenkové jsou chovatelsky velmi nároční a choulostiví, a tak lze považovat za úspěch, že první jedinec, který dorazil v roce 1939 z přírody, žil v zoo celé dva roky. V 50. a 60. letech prošlo zoologickou zahradou mnoho zvířat, o jejichž dalším osudu se záznamy většinou nedochovaly. Poté se fenkové s menšími přestávkami objevovali v chovech zoo stále a v roce 1998 se podařil první zaznamenaný úspěšný odchov.'),
(64019, 2993, 'expozice', 'Afrika zblízka'),
(64020, 2993, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/pavilony/1443-afrika-zblizka'),
(64021, 2994, 'id', '108'),
(64022, 2994, 'latin_title', 'Gymnorhina tibicen hypoleuca'),
(64023, 2994, 'trida', 'Ptáci (Aves)'),
(64024, 2994, 'skupina', 'Pěvci (Passeriformes)'),
(64025, 2994, 'popisek', 'Je to středně velký pěvec s robustním tělem, silným zobákem a zlatohnědýma očima. Tělo je převážně černé, s bílými plochami pod ocasem, na šíji a na hřbetě.\r\n\r\n \r\n '),
(64026, 2994, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/flatn_k_australskz_3_-_Tom_A_Adamec.jpg'),
