(64366, 3012, 'skupina', 'Hrabáči (Tubulidentata)'),
(64367, 3012, 'popisek', 'Hrabáč má nahrbené tělo, malou hlavu s protáhlým čenichem zakončeným rozšířeným rypákem, velké kornoutovité boltce. Má svalnaté nohy, na předních má čtyři prsty a na zadních pět. Jsou zakončené silnými drápy.\r\n'),
(64368, 3012, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/hrab_u_kapskz_-_archiv_zoo.jpg'),
(64369, 3012, 'obrazek_popis', 'Hrabáč, foto: archiv Zoo Praha'),
(64370, 3012, 'kontinent', 'Afrika'),
(64371, 3012, 'kontinent-detail', 'subsaharská Afrika'),
(64372, 3012, 'biotop', 'travnaté území'),
(64373, 3012, 'biotop-detail', 'suchá savana, buš'),
