(64534, 3021, 'id', '131'),
(64535, 3021, 'latin_title', 'Capromys pilorides'),
(64536, 3021, 'trida', 'Savci (Mammalia)'),
(64537, 3021, 'skupina', 'Hlodavci (Rodentia)'),
(64538, 3021, 'popisek', 'Má zavalité tělo, hranatou hlavu a poměrně krátké nohy se štíhlými prsty a ostrými drápy´, které se uplatní při šplhání po stromech. Základní zbarvení je hnědé, hřbet má rezavý nádech.\r\n\r\n \r\n \r\n '),
(64539, 3021, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/0017-0975.jpg'),
(64540, 3021, 'obrazek_popis', 'Hutia-konga, foto: Rostislav Stach'),
(64541, 3021, 'kontinent', 'Jižní Amerika'),
(64542, 3021, 'kontinent-detail', 'Kuba'),
(64543, 3021, 'biotop', 'tropický les'),
(64544, 3021, 'biotop-detail', 'různé typy lesů'),
