(67071, 3154, 'biotop-detail', 'slaná i sladká jezera, laguny a mokřady do výšky 4 500 m n. m.'),
(67072, 3154, 'potrava', 'bezobratlí'),
(67073, 3154, 'potrava-detail', 'drobní vodní bezobratlí a mikroskopické řasy'),
