(68241, 3216, 'trida', 'Plazi (Reptilia)'),
(68242, 3216, 'skupina', 'Šupinatí (Squamata)'),
(68243, 3216, 'popisek', 'Jasně zelené zbarvení varana smaragdového je přizpůsobením k životu v korunách stromů.\r\n'),
(68244, 3216, 'obrazek', 'https://zoopraha.cz/images/lexikon-images/varan_smaragdovy_1.jpg'),
