INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(68273, 3217, 'zajimavosti', 'Představuje jeden z poddruhů takzvaných stepních zeber. Vyskytuje se až do nadmořské výšky 4 500 m n. m., nejčastěji však stáda těchto zeber zastihneme v nižších polohách v savaně a buši, vždy poblíž vody, za kterou někdy putují i na velké vzdálenosti. V období migrací se sdružují do tisícových agregací, velmi často společně s pakoni i jinými antilopami. Základem společenského uspořádání stád jsou harémy vedené jedním hřebcem, jindy se jedná o skupiny klisen nebo mladých hřebečků. '),
(68274, 3217, 'chov', 'Zebry Böhmovy chovala pražská zoo ve dvou vlnách. Poprvé to bylo v letech 1949−1970, podruhé od roku 2004. V první etapě bylo odchováno 15 mláďat. V druhé jsme zpočátku chovali jen klisny, ke kterým se hřebec připojil v roce 2014.'),
(68275, 3217, 'expozice', 'Horní část zoo'),
(68276, 3217, 'expozice-odkaz', 'http://www.zoopraha.cz/zvirata-a-expozice/kam-v-zoo/areal/8661-horni-cast-arealu'),
